import { Preset } from 'unocss';

interface RemToRpxOptions {
    /**
     * Enable applet, only build applet should be true
     * e.g. In uniapp set `enable: !(process.env.UNI_PLATFORM === 'h5')` to disable for h5
     * @default true
     */
    enable?: boolean;
    /**
     * 1rem = n px
     * @default 16
     */
    baseFontSize?: number;
    /**
     * screen width in px
     * @default 375
     */
    screenWidth?: number;
}
declare function remToRpxPreset(options?: RemToRpxOptions): Preset;

export { RemToRpxOptions, remToRpxPreset as default };
